CREATE TABLE Venda(	numero int identity(1,1) not null,	cod_cliente int not null,	cod_funcionario int not null,	valor_venda float not null,	data_venda datetime not null,	condicao_pagamento varchar(10) not null,	CONSTRAINT pkvenda PRIMARY KEY (numero, cod_cliente, cod_funcionario),	CONSTRAINT fkvendacliente FOREIGN KEY (cod_cliente) REFERENCES Cliente(codigo),	CONSTRAINT fkvendafuncionario FOREIGN KEY (cod_funcionario) REFERENCES Funcionario(codigo),);